import React, { useState } from 'react';
import axios from 'axios';
import Chart from './Chart';

function Dashboard() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState(null);
  const [predictions, setPredictions] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleUpload = async () => {
    if (!file) return;
    
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('file', file);
      
      const response = await axios.post('http://localhost:5001/api/upload', formData, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      setData(response.data);
      setPredictions(null);
    } catch (error) {
      console.error("Upload failed:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Modern header */}
      <header className="mb-8 text-center">
        <h1 className="text-3xl font-bold text-gray-800 mb-2">Data Insights Dashboard</h1>
        <p className="text-gray-600">Upload your CSV for instant analysis</p>
      </header>

      {/* Upload card */}
      <div className="card max-w-2xl mx-auto">
        <div className="flex flex-col sm:flex-row gap-4 items-center">
          <input 
            type="file" 
            onChange={(e) => setFile(e.target.files[0])} 
            accept=".csv"
            className="file-input"
          />
          <button 
            onClick={handleUpload}
            disabled={loading}
            className="btn-primary flex-shrink-0"
          >
            {loading ? 'Processing...' : 'Upload & Analyze'}
          </button>
        </div>
      </div>

      {/* Results section */}
      {data && (
        <div className="space-y-8 mt-8">
          {/* Data summary card */}
          <div className="card">
            <h2 className="text-xl font-semibold mb-4 text-gray-700">Data Summary</h2>
            <div className="bg-gray-50 p-4 rounded-lg">
              <pre className="text-sm text-gray-800 overflow-x-auto">
                {JSON.stringify(data.summary, null, 2)}
              </pre>
            </div>
          </div>

          {/* Chart card */}
          {data.chart_data && (
            <div className="card">
              <h2 className="text-xl font-semibold mb-4 text-gray-700">Data Visualization</h2>
              <div className="p-4 bg-white rounded-lg border border-gray-200">
                <Chart chartData={data.chart_data} />
              </div>
              <button 
                onClick={() => {
                      // Add your prediction logic here
                      handlePredict(); // Make sure this function is defined
                        }}
                          className="btn-primary mt-4"
                          >
                              Generate Predictions
                              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
